              .content {
                display: grid;
                grid-template-rows: 100%;
                grid-template-columns: 100%;
                grid-template-areas: "main";
                height: calc(100vh - 19rem);
                width: 100%;
                margin: 0 auto;
                position: relative;
              }

              .content__slide {
