              }

              .content__img-inner {
                width: 100%;
                height: 100%;
                background-position: 50% 50%;
                background-size: cover;
              }

              .content__text-wrap {
                height: 100%;
                display: flex;
                flex-direction: column;
                align-items: center;
                justify-content: center;
              }

              .content__text {
                overflow: hidden;
                flex: 1;
                opacity: 0;
                pointer-events: none;
              }

              .content__text:nth-child(6) {
                opacity: 1;
              }

              .content__text--full {
                flex: none;
              }

              .content__text-inner {
                display: block;
                color: var(--color-slide-text);
                text-transform: uppercase;
                font-size: 3.5rem;
                font-weight: var(--font-trail-weight);
                line-height: var(--font-trail-lheight);
                font-family: var(--font-trail-family);
              }

              .content__text-inner--bottom {
                transform: translate3d(0, -40%, 0);
              }

              .content__text-inner--stroke {
                -webkit-text-stroke: 2px var(--color-slide-text);
                text-stroke: 2px var(--color-slide-text);
                -webkit-text-fill-color: transparent;
                text-fill-color: transparent;
                color: transparent;
              }

              .content__nav {
                grid-area: main;
                justify-self: center;
                align-self: center;
                padding: 22rem;
                width: calc(90% - 1rem);
                max-width: 1000px;
                height: auto;
                display: flex;
                align-items: flex-end;
                justify-content: center;
                font-size: 1rem;
                position: relative;
              }

