import Head from 'next/head'
import Container from '../components/container'
import MoreStories from '../components/more-stories'
import HeroPost from '../components/hero-post'
import HeroIntro from '../components/hero-intro'
import Intro from '../components/intro'
import Layout from '../components/layout'
import { getAllPostsForHome } from '../lib/api'
import { CMS_NAME } from '../lib/constants'

export default function Index({ allPosts }) {
  const heroPost = allPosts[0]
  const morePosts = allPosts.slice(1)
  return (
    <>
      <Layout>
        <Head>
          <title>Reemuu Designs by {CMS_NAME}</title>
        </Head>
        <Container>
          <Intro />
          <HeroIntro />
          <section className="grid grid-cols-1 text-center">
            <div>
              <h5 className="text-sm">HI, WE'RE REEMUU.</h5>
            </div>
            <div>
              <p className="max-w-xl text-3xl">
                We're a design and tech company that speaks all the jargon. But
                luckily, we speak human too. <em className="font-serif">We design, we write code, and we
                love the business side of it all.</em> We are hired to <strong>work on new
                problems together</strong>, instead of telling people what they want to
                hear.
              </p>
            </div>
          </section>
          {heroPost && (
            <HeroPost
              title={heroPost.title}
              coverImage={heroPost.coverImage}
              date={heroPost.date}
              author={heroPost.author}
              slug={heroPost.slug}
              excerpt={heroPost.excerpt}
            />
          )}
          {morePosts.length > 0 && <MoreStories posts={morePosts} />}
        </Container>
      </Layout>
    </>
  );
}

export async function getStaticProps({ preview = false }) {
  const allPosts = (await getAllPostsForHome(preview)) || []
  return {
    props: { allPosts },
  }
}
